mapper.select:
select * from blog
where publish =1

      and title like #{param3}
      or text like #{param3}

ORDER BY date DESC
LIMIT #{param1},#{param2}

mapper.update:
update blog
set star=#{param2}
where tid=#{param1}

mapper.select:
select count(*)from blog
where publish=1

  and title like #{param3}
  or text like #{param3}
